import React, { Component } from 'react';
class FlipCounter extends Component{
constructor(props){
    super(props);
    this.state={ 
     count0:0,
     count1:0,
     count2:0,
     count3:0,
     count4:0,
     count5:0,
     count6:0
    };


}

add  =() =>{
    if(this.state.count0==9){
        this.setState({count0: this.state.count0=-1 })
        this.setState({count1: this.state.count1+1 })
    };

    if(this.state.count1==9){
        this.setState({count1: this.state.count1=-1 })
        this.setState({count2: this.state.count2+1 })
    };

    if(this.state.count2==9){
        this.setState({count2: this.state.count2=-1 })
        this.setState({count3: this.state.count3+1 })
    };

    if(this.state.count3==9){
        this.setState({count3: this.state.count3=-1 })
        this.setState({count4: this.state.count4+1 })
    };


    if(this.state.count4==9){
        this.setState({count4: this.state.count4=-1 })
        this.setState({count5: this.state.count5+1 })
    };



    this.setState({
     count0: this.state.count0 + 1

    })

};



render(){
return(
<div>
<p>this is my counter: </p>
  <h1>
    <p style={{ letterSpacing:3  ,color: "white",background:"black",border: "white"}}>{this.state.count5}{this.state.count4}{this.state.count3}{this.state.count2}{this.state.count1}{this.state.count0}</p>  <p style={{ letterSpacing:3  ,color: "white",background:"black"}}>{this.state.count5}{this.state.count4}{this.state.count3}{this.state.count2}{this.state.count1}{this.state.count0}</p>
    </h1>
   <button onClick={this.add}>add</button>

</div>
);


}

}
export default FlipCounter;
