    };


}

add  =() =>{
    if(this.state.count0==9){
        this.setState({count0: this.state.count0=-1 })
        this.setState({count1: this.state.count1+1 })
    }

    ;
    this.setState({
     count0: this.state.count0 + 1

    })

};



render(){
return(
<div>
    <p>this is my counter: {this.state.count5}{this.state.count4}{this.state.count3}{this.state.count2}{this.state.count1}{this.state.count0}</p>
