import React, { Component } from 'react';
class FlipCounter extends Component{
constructor(props){
    super(props);
    this.state={ 
     count0:0,
     count1:0,
     count2:0,
     count3:0,
     count4:0,
     count5:0,
     count6:0
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    }
add  =() =>{
    if(this.state.count0==9){
        this.setState({count0: this.state.count0=-1 })
        this.setState({count1: this.state.count1+1 })
    };

    if(this.state.count1==9){
        this.setState({count1: this.state.count1=-1 })
        this.setState({count2: this.state.count2+1 })
    };

    if(this.state.count2==9){
        this.setState({count2: this.state.count2=-1 })
        this.setState({count3: this.state.count3+1 })
    };

    if(this.state.count3==9){
        this.setState({count3: this.state.count3=-1 })
        this.setState({count4: this.state.count4+1 })
    };


    if(this.state.count4==9){
        this.setState({count4: this.state.count4=-1 })
        this.setState({count5: this.state.count5+1 })
    };



    this.setState({
     count0: this.state.count0 + 1

    })

};




handleChange(event) {
this.setState({value: event.target.value});
}

handleSubmit(event) {
alert('A name was submitted: ' + this.state.value);
this.setState({count0: this.state.value%10 })

event.preventDefault();
}


render(){
return(
<div>
<p>this is my counter: </p>
  <h1>
   <table>
<tr>
<td style={{ letterSpacing:3  ,color: "white",background:"black"}}>{this.state.count5}</td>
<td style={{ letterSpacing:3  ,color: "white", background:"black"}}>{this.state.count4}</td>
<td style={{ letterSpacing:3  ,color: "white", background:"black"}}>{this.state.count3}</td>
<td style={{ letterSpacing:3  ,color: "white", background:"black"}}>{this.state.count2}</td>
<td style={{ letterSpacing:3  ,color: "white", background:"black"}}>{this.state.count1}</td>
<td style={{ letterSpacing:3  ,color: "white",background:"black"}}>{this.state.count0}</td>
</tr>

   </table>
     </h1>
   <button onClick={this.add}>add</button>

<p>
<form on submit={this.submit}>
<input type="text" ></input>
<button onClick={this.setNumberOnCounter}>insert a number</button>
</form>
</p> 



<form onSubmit={this.handleSubmit}>
          <label>
          insert number
            <input type="text" value={this.state.value} onChange={this.handleChange} />
          </label>
          <input type="submit" value="Submit" />
        </form>
</div>



);


}

}
export default FlipCounter;
